// This is a copy of the first part of the SqliteDb structure in tclsqlite.c.  We need it here so that the get_sqlite_pointer routine
// can extract the sqlite3* pointer from an existing Tcl SQLite connection.
struct TestCtx
{
	Context *Ctx;
};

// Convert text generated by the "%p" conversion format back into a pointer.
__device__ static int testHexToInt(int h)
{
	if (h >= '0' && h <= '9')
		return h - '0';
	else if (h >= 'a' && h <= 'f')
		return h - 'a' + 10;
	else
	{
		_assert(h >= 'A' && h <= 'F');
		return h - 'A' + 10;
	}
}

__device__ void *sqlite3TestTextToPtr(const char *z)
{
	if (z[0] == '0' && z[1] == 'x')
		z += 2;
	uint64 v = 0;
	while (*z)
	{
		v = (v<<4) + testHexToInt(*z);
		z++;
	}
	void *p;
	if (sizeof(p) == sizeof(v))
		_memcpy(&p, &v, sizeof(p));
	else
	{
		uint32 v2;
		_assert(sizeof(p) == sizeof(v2));
		v2 = (uint32)v;
		_memcpy(&p, &v2, sizeof(p));
	}
	return p;
}


// A TCL command that returns the address of the sqlite* pointer for an sqlite connection instance.  Bad things happen if the
// input is not an sqlite connection.
__device__ static int get_sqlite_pointer(ClientData clientData, Tcl_Interp *interp, int argc, const char *args[])
{
	if (argc != 2)
	{
		Tcl_WrongNumArgs(interp, 1, args, "SQLITE-CONNECTION");
		return TCL_ERROR;
	}
	Tcl_CmdInfo cmdInfo;
	if (!Tcl_GetCommandInfo(interp, args[1], &cmdInfo))
	{
		Tcl_AppendResult(interp, "command not found: ", args[1], nullptr);
		return TCL_ERROR;
	}
	struct TclContext *p = (struct TclContext *)cmdInfo.objClientData;
	char b[100];
	_sprintf(b, "%p", p->Ctx);
	if (_strncmp(b, "0x", 2))
		_sprintf(b, "0x%p", p->Ctx);
	Tcl_AppendResult(interp, b, 0);
	return TCL_OK;
}

// Decode a pointer to an sqlite3 object.
__device__ int getDbPointer(Tcl_Interp *interp, const char *a, Context **ctx)
{
	struct TclContext *p;
	Tcl_CmdInfo cmdInfo;
	if (Tcl_GetCommandInfo(interp, a, &cmdInfo))
	{
		p = (struct TclContext *)cmdInfo.objClientData;
		*ctx = p->Ctx;
	}
	else
		*ctx = (Context *)sqlite3TestTextToPtr(a);
	return TCL_OK;
}

