// Usage:  sqlite3_exec  DB  SQL
//
// Invoke the sqlite3_exec interface using the open database DB
__device__ static int test_exec(ClientData notUsed, Tcl_Interp *interp, int argc, const char *args[])
{
	if (argc != 3)
	{
		Tcl_AppendResult(interp, "wrong # args: should be \"", args[0], " DB SQL", nullptr);
		return TCL_ERROR;
	}
	Context *ctx;
	if (getDbPointer(interp, args[1], &ctx)) return TCL_ERROR;
	char *sql = _mprintf("%s", args[2]);
	int i, j;
	for (i = j = 0; sql[i];)
		if (sql[i]=='%' )
		{
			sql[j++] = (testHexToInt(sql[i+1])<<4) + testHexToInt(sql[i+2]);
			i += 3;
		}
		else
			sql[j++] = sql[i++];
	sql[j] = 0;
	TextBuilder str;
	TextBuilder::Init(&str);
	char *err = nullptr;
	RC rc = Main::Exec(ctx, sql, exec_printf_cb, &str, &err);
	_free(sql);
	char b[30];
	_sprintf(b, "%d", rc);
	Tcl_AppendElement(interp, b);
	Tcl_AppendElement(interp, rc == RC_OK ? str.ToString() : err);
	str.Reset();
	if (err) _free(err);
	if (sqlite3TestErrCode(interp, ctx, rc)) return TCL_ERROR;
	return TCL_OK;
}

